import React, { useContext, useState } from 'react';
import { AppContext } from '../context/AppContext';

const DepSelected = (props) => {
    const { dispatch} = useContext(AppContext);

    const [name, setName] = useState('');
    const [quantity, setQuantity] = useState('');
    const [action, setAction] = useState('');


    const submitEvent = () => {

        const item = {
            name: name,
            quantity: parseInt(quantity),
        };
        //const dep = ["IT", "HR", "MR"];
        const dep = {
            department: [
                { index: "1", name: 'IT'},
                { index: "2", name: 'HR'},
                { index: "3", name: 'MR'},]
        };
        if(action === "Reduce") {
            dispatch({
                type: 'RED_QUANTITY',
                payload: item,
            });
        } else {
                dispatch({
                    type: 'ADD_QUANTITY',
                    payload: item,
                });
            }
    };

    return (

        <div>
             <p>{props}</p>
            <div className='row'>

            <div className="input-group mb-3" style={{ marginLeft: '2rem' }}>
                    <div className="input-group-prepend">
                <label className="input-group-text" htmlFor="inputGroupSelect01">Department: </label>
                </div>
                  <select className="custom-select" id="inputGroupSelect01" onChange={(event) => setName(event.target.value)}>
                        <option defaultValue>Choose...</option>
                        <option value="Shirt" name="Shirt"> IT Department</option>
                <option value="Dress" name="Dress">Finance Department</option>
                <option value="Jeans" name="Jeans">Human Resources Department</option>
                <option value="Dinner set" name="Dinner set">Sales Department</option>
                <option value="Bags" name="Bags">Marketing Department</option>
                  </select>

                    <div className="input-group-prepend" style={{ marginLeft: '2rem' }}>
                <label className="input-group-text" htmlFor="inputGroupSelect02">Allocation</label>
                </div>
                  <select className="custom-select" id="inputGroupSelect02" onChange={(event) => setAction(event.target.value)}>
                  <option defaultValue value="Add" name="Add">Add</option>
                <option value="Reduce" name="Reduce">Reduce</option>
                  </select>  
                  <span className="eco" style={{ marginLeft: '2rem', marginRight: '8px'}}></span>

                    <input
                        required='required'
                        type='text'
                        id='cost'
                        value={quantity}
                        style={{size: 10}}
                        onChange={(event) => setQuantity(event.target.value)}>
                        </input>

                    <button className="btn btn-primary" onClick={submitEvent} style={{ marginLeft: '2rem' }}>
                        Save
                    </button>
                </div>
                </div>

        </div>
    );
};

export default DepSelected;




